import React, { useState } from 'react';
import { searchRecipes } from '../services/api';

export default function SearchBar({ onResults }) {
  const [q, setQ] = useState('');
  const [error, setError] = useState('');
  const [info, setInfo] = useState('');
  const [loading, setLoading] = useState(false);

  const doSearch = async () => {
    setError('');
    setInfo('');
    const trimmed = q.trim();
    if (!trimmed) {
      setError('Please enter a search term.');
      onResults([]);
      return;
    }
    if (trimmed.length < 3) {
      setError('Search term must be at least 3 characters.');
      onResults([]);
      return;
    }
    setLoading(true);
    try {
      const data = await searchRecipes(trimmed);
      if (Array.isArray(data) && data.length === 0) {
        setInfo('No recipes found for your search.');
      }
      onResults(Array.isArray(data) ? data : []);
    } catch (err) {
      setError(err.message || 'Search failed');
      onResults([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="searchbar">
      <input
        value={q}
        onChange={e => setQ(e.target.value)}
        placeholder="Search recipes by name or cuisine"
        aria-label="Search recipes"
      />
      <button onClick={doSearch} disabled={loading}>
        {loading ? 'Searching...' : 'Search'}
      </button>

      {error && <div style={{ color: 'red', marginTop: '5px' }}>{error}</div>}
      {info && <div style={{ color: 'blue', marginTop: '5px' }}>{info}</div>}
    </div>
  );
}
